con:configuration.scriptText:
import groovy.json.JsonSlurper
import jxl.*
import java.io.*
import jxl.write.*
def httpResponseHeaders = context.testCase.testSteps["GetCustomerDetails10"].testRequest.response.responseHeaders
def httpStatus = httpResponseHeaders["#status#"]
def httpStatusCode = (httpStatus =~ "[1-5]\\d\\d")[0]
log.info("HTTP status code: " + httpStatusCode)
if(httpStatusCode=="200")
    {
        log.info("Status matches, Test case is passed")
    }
    else
    {
        log.info ("Test case failed")
    }

def response10 = context.testCase.getTestStepByName("GetCustomerDetails10").getPropertyValue("Response")
def obj = new JsonSlurper().parseText(response10)
def custid_resp= obj.data.customer_id
def rewardpoints_resp = obj.data.reward_points
def rew = rewardpoints_resp.toString()
def cust = custid_resp.toString()
log.info ("Customer id from response is: " + cust)
def custid_custpro = context.testCase.getPropertyValue("id")
log.info ("Customer id from custom property file is: " +custid_custpro)
if(cust == custid_custpro)
{
	log.info("Customer id from response matches with that of the custom property file")
}
else
{
	log.info("Customer id is not matching. Test case failed")	
}


log.info ("Reward points from response is: " + rew)
if(rew != "0")
{
	log.info("Reward points are added, TEST CASE PASSED!!!! Reward points: "+rew)
}
else
{
	log.info("Reward points are not added. Test case failed")	
}

