//let make it work for database
import com.eviware.soapui.support.GroovyUtils
import groovy.json.JsonSlurper
import groovy.sql.Sql
def accessToken = "6cd7568021c2548726acb981e2ef6e95781fe17b";
//Connect to database
//URL,user,password,Driver
def url = 'jdbc:mysql://localhost:3306/SDET3'
def user = 'root'
def password ='mariapwd123'
def driver = 'com.mysql.jdbc.Driver'
//if i want my SOAPUI to talk to database and fetch information then i require JDBC driver
com.eviware.soapui.support.GroovyUtils.registerJdbcDriver(driver)
//I will connect to my database
def sql = Sql.newInstance(url,user,password,driver)

def tc1=context.testCase.getTestStepByName("AddNewCategory11")
def tc2=context.testCase.getTestStepByName("GetCategoryDetails11")

//SQL Query to execute
sql.eachRow("select * from category")
{
    row ->
    def categoryname = "$row.categoryname"
    def metatitle = "$row.metatitle"
    log.info "The Category name is: "+categoryname
    log.info "Meta title is: "+metatitle
    context.testCase.getTestStepByName("Properties11").setPropertyValue("category_name",categoryname)
    context.testCase.getTestStepByName("Properties11").setPropertyValue("meta_title",metatitle)
    tc1.run(testRunner,context)
    tc2.run(testRunner,context)
    def response = context.testCase.getTestStepByName("AddNewCategory11").getPropertyValue("Response")
    def obj = new JsonSlurper().parseText(response)
    def id = obj.data.id
    log.info id
    context.testCase.setPropertyValue("id",id.toString())
}

